import React, { useState, useContext, useEffect } from 'react';
import { Link } from "react-router-dom";

import PageHelmet from "../component/common/Helmet";
import AlertContext from "../context/alerts/alertContext";
import AuthContext from '../context/authentication/authContext';

const Contact = (props) => {
    //Extract values from context
    const alertContext = useContext(AlertContext);
    const { alert, showAlert } = alertContext;

    const authContext = useContext(AuthContext);
    const { message, authenticated, registerUser } = authContext

    //In case that the user already exists
    useEffect( () => {
        if(authenticated) {
            props.history.push('/ready');
        }
        if(message) {
            showAlert(message.msg, message.category);
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [message, authenticated, props.history]);

    //State for login
    const [user, saveUser] = useState({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        category: "",
        pay: "",
    });

    //extract from user
    const { firstName, lastName, email, password, category, pay } = user;

    const onChange = e => {
        saveUser({
            ...user,
            [e.target.name]:e.target.value
        })
    };

    //When the user login
    const onSubmit = e => {
        e.preventDefault();

        //validate that there are no empty fields
        if(
            firstName.trim() === '' ||
            lastName.trim() === '' ||
            email.trim() === '' ||
            category.trim() === '' ||
            password.trim() === '' ||
            pay.trim() === ''
        ) {
            showAlert('All fields are required', 'alert')
            return;
        };

        //Passwors with min of 6 characters
        if(password.length < 6) {
            showAlert('The password must be at least 6 characters', 'alert');
            return;
        };

        //pass it to action
        registerUser({
            firstName,
            lastName,
            email,
            password,
            category,
            pay,
        })
    };

    return(
        <React.Fragment>
            <PageHelmet pageTitle='Contact' />

                {/* Start Slider Area   */}
                <div className="slider-wrapper" id="contacto">
                    <div className="form-usuario">
                        <div className="contenedor-form sombra-dark">
                            { alert ? (<div className={`alert ${alert.category}`}> {alert.msg} </div>) : null }
                            <h1>Create Account</h1>
                            <form onSubmit={onSubmit}>
                                <div className="campo-form">
                                    <label htmlFor="nombre">First Name</label>
                                    <input
                                        type="text"
                                        id="firstName"
                                        name="firstName"
                                        placeholder="* You're first name"
                                        value={firstName}
                                        onChange={onChange}
                                    />
                                </div>
                                <div className="campo-form">
                                    <label htmlFor="nombre">Last Name</label>
                                    <input
                                        type="text"
                                        id="lastName"
                                        name="lastName"
                                        placeholder="* You're last name"
                                        value={lastName}
                                        onChange={onChange}
                                    />
                                </div>
                                <div className="campo-form">
                                    <label htmlFor="email">Email</label>
                                    <input
                                        type="email"
                                        id="email"
                                        name="email"
                                        placeholder="* You're email"
                                        value={email}
                                        onChange={onChange}
                                    />
                                </div>

                                <div className="campo-form">
                                    <label htmlFor="category">category</label>
                                    <input
                                        type="category"
                                        id="category"
                                        name="category"
                                        placeholder="* You're category"
                                        value={category}
                                        onChange={onChange}
                                    />
                                </div>

                                <div className="campo-form">
                                    <input
                                        type="submit"
                                        className="btn btn-primario btn-block"
                                        value="Register"
                                    />
                                </div>
                            </form>
                            <Link to="/" className="enlace-cuenta">Login</Link>
                        </div>
                    </div>
                    {/* End Single Slide */}
                </div>
                {/* End Slider Area   */}
                {/* Start Slider Area   */}
                <div className="slider-wrapper" id="contacto">
                    {/* Start Single Slide */}
                    <div className="d-flex align-items-center justify-content-center slide-style-1 slider-fixed--height bg_image bg_image--32" data-black-overlay="7">
                        <div className="contact-form--1">
                            <div className="container">
                                <div className="row row--35 align-items-start">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-12">
                                        <div className="rn-address">
                                            <div className="header-wrapper" id="header-wrapper">
                                                <div className="d-flex align-items-center justify-content-center mb--30">
                                                    <div className="logo">
                                                        <a href="#">
                                                            <img className="logo-1" src="/assets/images/logo/logonodo.png" alt="Logo Images"/>
                                                        </a>
                                                    </div>
                                                </div>
                                            </div>
                                            <div className="section-title text-center mb--30">
                                                <h2 className="title theme-gradient">Nodo Challenge</h2>
                                            </div>
                                            <div className="form-wrapper">
                                                <form>
                                                    <label htmlFor="nombre">First Name</label>
                                                    <input
                                                        type="text"
                                                        id="firstName"
                                                        name="firstName"
                                                        placeholder="* You're first name"
                                                        value={firstName}
                                                        onChange={onChange}
                                                    />
                                                    <label htmlFor="item01">
                                                        <input
                                                            type="text"
                                                            name="firtName"
                                                            id="item01"
                                                            value={firstName}
                                                        onChange={onChange}
                                                            placeholder="Nombre *"
                                                        />
                                                    </label>

                                                    {/* <label htmlFor="item02">
                                                        <input
                                                            type="text"
                                                            name="lastName"
                                                            id="item02"
                                                            value={this.state.rnLastName}
                                                            onChange={(e)=>{this.setState({rnLastName: e.target.value});}}
                                                            placeholder="Apellido *"
                                                        />
                                                    </label>

                                                    <label htmlFor="item03">
                                                        <input
                                                            type="text"
                                                            name="email"
                                                            id="item03"
                                                            value={this.state.rnEmail}
                                                            onChange={(e)=>{this.setState({rnEmail: e.target.value});}}
                                                            placeholder="Email *"
                                                        />
                                                    </label>

                                                    <label  htmlFor="item04">
                                                        <select
                                                            type="select"
                                                            name="category"
                                                            id="item04"
                                                            value={this.state.rnCategory}
                                                            onChange={(e)=>{this.setState({rnCategory: e.target.value});}}
                                                            placeholder="Categoria *"
                                                        >
                                                            <option value="categorias...">categorias...</option>
                                                        </select>
                                                    </label>

                                                    <label  htmlFor="item05">
                                                        <select
                                                            type="select"
                                                            name="pay"
                                                            id="item05"
                                                            value={this.state.rnPay}
                                                            onChange={(e)=>{this.setState({rnPay: e.target.value});}}
                                                            placeholder="Forma de pago *"
                                                        >
                                                            <option value="pago...">pago...</option>
                                                        </select>
                                                    </label> */}

                                                    <br/>

                                                    <button className="rn-button-style--2 btn-solid" type="submit" value="submit" name="submit" id="mc-embedded-subscribe">Enviar</button>
                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* End Single Slide */}
                </div>
                {/* End Slider Area   */}
            </React.Fragment>
        )

}
export default Contact
