import React, { Component , Fragment } from "react";
import { Link } from 'react-router-dom';
import { Parallax } from "react-parallax";
import Scrollspy from 'react-scrollspy';
// import GoogleMapReact from 'google-map-react';
import Slider from "react-slick";
import TextLoop from "react-text-loop";
import ScrollToTop from 'react-scroll-up';
import { FiHeadphones , FiMail , FiMapPin , FiChevronUp, FiCheck, FiX , FiMenu  } from "react-icons/fi";
import { slideSlick } from "../page-demo/script";
// import Header from "../component/header/Header";
import Footer from "../component/footer/Footer";
import Testimonial from "../elements/Testimonial";
import BlogContent from "../elements/blog/BlogContent";
import Helmet from "../component/common/Helmet";
// import ModalVideo from 'react-modal-video';
import { videoTagString, VideoTag } from 'react-video-tag';
import CounterOne from "../elements/counters/CounterOne";
import ServiceTwo from "../elements/service/ServiceTwo";
import TabTwo from "../elements/tab/TabTwo";
videoTagString({ src: '/assets/images/service/video.mp4' });
videoTagString({ src: '/assets/images/service/video3.mp4' });

const SlideList = [
    {
        textPosition: 'text-center',
        bgImage: 'bg_image--11',
        category: '',
        title: 'Nodo sur',
        description: 'En Nodo Sur te ayudamos a desarrollar habilidades para llevar tus entrenamientos a otro nivel.',
        buttonText: 'Prueba con nosotros',
        buttonLink: '#'
    },
    {
        textPosition: 'text-right',
        bgImage: 'bg_image--23',
        category: '',
        title: 'Feel the burn',
        description: 'En Nodo Sur te ayudamos a desarrollar habilidades para llevar tus entrenamientos a otro nivel.',
        buttonText: 'Horarios y Precios',
        buttonLink: '#'
    },
    {
        textPosition: 'text-left',
        bgImage: 'bg_image--21',
        category: '',
        title: 'Descubre el Crossfit',
        // description: 'Centro de entrenamiento funcional.',
        buttonText: 'Contáctanos',
        buttonLink: '#'
    }
]

const starndardService = [
    {
        image: '01',
        title: 'Membresía Crossfit',
        description: 'La membresía le brinda acceso completo a todas nuestras clases e instalaciones.',
    },
    {
        image: '02',
        title: 'Próxima generación',
        description: 'Clases para niños y adolescentes',
    },
    {
        image: '03',
        title: 'Energía renovada',
        description: 'Clase de vitalidad',
    },
]

const SlideVideo = [
    {
        textPosition: 'text-left',
        category: '',
        title: 'Bienvenidos a',
        description: 'There are many variations of passages of Lorem Ipsum available but the majority have suffered alteration.',
        buttonText: 'Elige tu entrenamiento',
        buttonLink: '#'
    }
]

const image6 =
    "/assets/images/bg/bg-image-15.jpg";

class DigitalAgency extends Component{
    constructor () {
        super()
        this.state = {
            rnName: '',
            rnEmail: '',
            rnMessage: '',
        };
        this.menuTrigger = this.menuTrigger.bind(this);
        this.CLoseMenuTrigger = this.CLoseMenuTrigger.bind(this);
        // this.stickyHeader = this.stickyHeader.bind(this);
       //  this.subMetuTrigger = this.subMetuTrigger.bind(this);
        window.addEventListener('load', function() {
            console.log('All assets are loaded')
        })
    }

    menuTrigger() {
        document.querySelector('.header-wrapper').classList.toggle('menu-open')
    }

    CLoseMenuTrigger() {
        document.querySelector('.header-wrapper').classList.remove('menu-open');
    }
    static defaultProps = {
        center: {
            lat: 10.5883286,
            lng: -71.6327717,
        },
        zoom: 11
    };
    // stickyHeader () {}
    render(){
        var elements = document.querySelectorAll('.has-droupdown > a');
        for(var i in elements) {
            if(elements.hasOwnProperty(i)) {
                elements[i].onclick = function() {
                    this.parentElement.querySelector('.submenu').classList.toggle("active");
                    this.classList.toggle("open");
                }
            }
        }
        // window.addEventListener('scroll', function() {
        //     var value = window.scrollY;
        //     if (value > 50) {
        //         document.querySelector('.header--fixed').classList.add('sticky')
        //     }else{
        //         document.querySelector('.header--fixed').classList.remove('sticky')
        //     }
        // });
        const { logo, color='default-color' } = this.props;
        let logoUrl;
        if(logo === 'light'){
            logoUrl = <img src="/assets/images/logo/logonodo.png" alt="Digital Agency" />;
        }else if(logo === 'dark'){
            logoUrl = <img src="/assets/images/logo/logonodo.png" alt="Digital Agency" />;
        }else if(logo === 'symbol-dark'){
            logoUrl = <img src="/assets/images/logo/logonodo.png" alt="Digital Agency" />;
        }else if(logo === 'symbol-light'){
            logoUrl = <img src="/assets/images/logo/logonodo.png" alt="Digital Agency" />;
        }else{
            logoUrl = <img src="/assets/images/logo/logonodo.png" alt="Digital Agency" />;
        }
        const PostList = BlogContent.slice(0 , 3);
        let title = 'Horario'
        return(
            <Fragment>
                <Helmet pageTitle="Nodo Sur" />

                {/* Start Header Area  */}
                <header className="header-area formobile-menu header--fixed default-color">
                    <div className="header-wrapper" id="header-wrapper">
                        <div className="header-left">
                            <div className="logo">
                                <a href="#">
                                    <img className="logo-1" src="/assets/images/logo/logonodo.png" alt="Logo Images"/>
                                </a>
                            </div>
                        </div>
                        <div className="header-right">
                            <nav className="mainmenunav d-lg-block">
                                <Scrollspy className="mainmenu" items={['crossfit','nosotros','servicios', 'horario','equipo','comunidad','plan','contacto']} currentClassName="is-current" offset={-200}>
                                    <li><a href="#crossfit">Crossfit</a></li>
                                    <li><a href="#nosotros">Nosotros</a></li>
                                    <li><a href="#servicios">Servicios</a></li>
                                    <li><a href="#horario">Horario</a></li>
                                    <li><a href="#equipo">Equipo</a></li>
                                    <li><a href="#comunidad">Comunidad</a></li>
                                    <li><a href="#plan">plan</a></li>
                                    <li><a href="#contacto">Contacto</a></li>
                                    {/* <li className="has-droupdown"><Link to="#">Home</Link>
                                        <ul className="submenu">
                                            <li><Link to="#">Main Demo</Link></li>
                                        </ul>
                                    </li> */}
                                </Scrollspy>
                            </nav>
                            {/* <div className="header-btn">
                                <a className="rn-btn" href="#">
                                    <span>now</span>
                                </a>
                            </div> */}
                            {/* Start Humberger Menu  */}
                            <div className="humberger-menu d-block d-lg-none pl--20">
                                <span onClick={this.menuTrigger} className="menutrigger text-white"><FiMenu /></span>
                            </div>
                            {/* End Humberger Menu  */}
                            <div className="close-menu d-block d-lg-none">
                                <span onClick={this.CLoseMenuTrigger} className="closeTrigger"><FiX /></span>
                            </div>
                        </div>
                    </div>
                </header>
                {/* End Header Area  */}

                {/* Start Slider Area   */}
                <div className="slider-wrapper color-white">
                    <div className="slider-activation slider-digital-agency">
                        <Slider className="rn-slick-dot dot-light" {...slideSlick}>
                            {SlideList.map((value , index) => (
                                <div className={`slide slide-style-2 fullscreen d-flex align-items-center justify-content-center bg_image ${value.bgImage}`} key={index} data-black-overlay="6">
                                    <div className="container">
                                        <div className="row">
                                            <div className="col-lg-12">
                                                <div className={`inner ${value.textPosition}`}>
                                                    {value.category ? <span>{value.category}</span> : ''}
                                                    {value.title ? <h1 className="title">{value.title}</h1> : ''}
                                                    {value.description ? <p className="description">{value.description}</p> : ''}
                                                    {value.buttonText ? <div className="slide-btn"><a className="rn-button-style--2 btn-primary-color" href={`${value.buttonLink}`}>{value.buttonText}</a></div> : ''}
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </Slider>
                    </div>
                </div>
                {/* End Slider Area   */}

                {/* Start Footer Style  */}
                <Footer />
                {/* End Footer Style  */}

                {/* Start Back To Top */}
                <div className="backto-top">
                    <ScrollToTop showUnder={160}>
                        <FiChevronUp />
                    </ScrollToTop>
                </div>
                {/* End Back To Top */}
            </Fragment>
        )
    }
}

export default DigitalAgency;
