import React, { Component } from "react";
import PageHelmet from "../component/common/Helmet";
import Select from 'react-select';

const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
]
class Contact extends Component{
    constructor () {
        super()
        this.state = {
            rnName: '',
            rnEmail: '',
            rnMessage: '',
        };
    }
    static defaultProps = {
        center: {
            lat: 59.95,
            lng: 30.33
        },
        zoom: 11
    };

    render(){
        return(
            <React.Fragment>
                <PageHelmet pageTitle='Contact' />

                {/* Start Header Area  */}
                <header className="header-area formobile-menu header--fixed default-color">
                    <div className="header-wrapper" id="header-wrapper">
                        <div className="header-left">
                            <div className="logo">
                                <a href="#">
                                    <img className="logo-1" src="/assets/images/logo/logonodo.png" alt="Logo Images"/>
                                </a>
                            </div>
                        </div>
                    </div>
                </header>
                {/* End Header Area  */}

                {/* Start Slider Area   */}
                <div className="slider-wrapper" id="contacto">
                    {/* Start Single Slide */}
                    <div className="slide slide-style-2 d-flex align-items-center justify-content-center slide-style-1 slider-fixed--height bg_image bg_image--1" data-black-overlay="5">
                        <div className="contact-form--1">
                            <div className="container">
                                <div className="row row--35 align-items-start">
                                    <div className="col-lg-12 col-md-12 col-sm-12 col-12">
                                        <div className="rn-address">
                                            <div className="section-title text-center mb--30">
                                                <h2 className="title theme-gradient">Nodo Challenge</h2>
                                            </div>
                                            <div className="form-wrapper">
                                                <form>
                                                    <label htmlFor="item01">
                                                        <input
                                                            type="text"
                                                            name="name"
                                                            id="item01"
                                                            value={this.state.rnName}
                                                            onChange={(e)=>{this.setState({rnName: e.target.value});}}
                                                            placeholder="Nombre *"
                                                        />
                                                    </label>

                                                    <label htmlFor="item02">
                                                        <input
                                                            type="text"
                                                            name="email"
                                                            id="item02"
                                                            value={this.state.rnEmail}
                                                            onChange={(e)=>{this.setState({rnEmail: e.target.value});}}
                                                            placeholder="Email *"
                                                        />
                                                    </label>

                                                    <label htmlFor="item05">
                                                        <Select
                                                            type="text"
                                                            name="select"
                                                            id="item05"
                                                            options={options}
                                                        />
                                                    </label>

                                                    <button className="rn-button-style--2 btn-solid" type="submit" value="submit" name="submit" id="mc-embedded-subscribe">Enviar</button>
                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* End Single Slide */}
                </div>
                {/* End Slider Area   */}
            </React.Fragment>
        )
    }
}
export default Contact
